
	//Assending order with 1
public class AssendingOrder {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1;i<=5;i++) {
			for(int j = 1; j<=i;j++) {
				System.out.print("1");
			}
			System.out.println("");
		}
	}
}

