		// Assending order with Doller
	public class AssendingOrder_with_Doller {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1;i<=5;i++) {      //assending Order
			for(int j = 1; j<=i;j++) {
				System.out.print("$");
			}
			System.out.println("");
		}
	}
}



