		

		//Type-1- Assending order with star
public class AssendingOrder_with_Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1;i<=5;i++) {			//Assending order
			for(int j = 1; j<=i;j++) {
				System.out.print("*");
			}
			System.out.println("");
		}

	}
}

